import React, {useRef} from 'react';
import './search-bar.css'
import {Container, Col, Form, FormGroup} from 'reactstrap';

import { BASE_URL } from '../utils/config';
import { useNavigate } from 'react-router-dom';

const SearchBar = () => {

    const locationRef = useRef('')
    const distanceRef = useRef(0)
    const maxGroupSizeRef = useRef(0)

    const searchHandler = async() => {

        const location = locationRef.current.value
        const distance = distanceRef.current.value
        const maxGroup = maxGroupSizeRef.current.value
        const navigate = useNavigate()

        if(location ==='' || distance ==='' || maxGroup ===''){
            return alert("All fields are required!");
        }

        const res = await fetch(`${BASE_URL}/tours/search/getTourBySearch?city=
        ${location}&distance=${distance}&maxGroupSize=${maxGroup}`)

        if(!res.ok) alert('Something went wronf')

        const result = await res.json()

        navigate
    }

  return (
    <Container className='search_bar_container'>
            <Col lg='12'>
            <div className="search_bar">
                <Form className="d-flex align-items-center gap-4">
                    <FormGroup className="location d-flex gap-3 form_group form_group-fast">
                        <span><i class="ri-map-pin-line"></i></span>
                        <div>
                            <h6>Location</h6>
                            <input type="text" placeholder="To"
                            ref={locationRef}/>
                        </div>
                    </FormGroup>
                    <FormGroup className="distance d-flex gap-3 form_group form_group-fast">
                        <span><i class="ri-map-pin-time-line"></i></span>
                        <div>
                            <h6>Distance</h6>
                            <input type="number" placeholder="Distance in km"
                                ref={distanceRef}/>
                        </div>
                    </FormGroup>
                    <FormGroup className="maxGroup d-flex gap-3 form_group form_group-last">
                        <span><i class="ri-group-line"></i></span>
                        <div>
                            <h6>Max People</h6>
                            <input type="number" placeholder="0"
                                ref={maxGroupSizeRef}/>
                        </div>
                    </FormGroup>

                    <span className="search_icon" type="submit" onClick={searchHandler}>
                        <i class="ri-search-line"></i>
                    </span>
                </Form>
            </div>
        </Col>
    </Container>
  )
}

export default SearchBar
