import React, { useRef, useEffect, useContext, useState } from "react";
import { Container, Row, Button } from "reactstrap";
import { NavLink, Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

import logo from "../../assets/images/logo.jpg";
import "./header.css";

const nav_links = [
  {
    path: "/home",
    display: "Home",
  },
  {
    path: "/about",
    display: "About",
  },
  {
    path: "/tour",
    display: "Tours",
  },
  {
    path: "/gallery",
    display: "Gallery",
  },
];

const Header = () => {
  const headerRef = useRef(null);
  const menuRef = useRef(null);
  const [activePath, setActivePath] = useState("");
  const { user, handleLogout } = useContext(AuthContext);
  console.log(user);

  const stickyHeaderFunc = () => {
    window.addEventListener("scroll", () => {
      if (
        document.body.scrollTop > 80 ||
        document.documentElement.scrollTop > 80
      ) {
        if (headerRef.current) {
          headerRef.current.classList.add("sticky_header");
        }
      } else {
        if (headerRef.current) {
          headerRef.current.classList.remove("sticky_header");
        }
      }
    });
  };

  useEffect(() => {
    stickyHeaderFunc();

    return () => {
      window.removeEventListener("scroll", stickyHeaderFunc);
    };
  }, []);

  const toggleMenu = () => {
    if (menuRef.current) {
      menuRef.current.classList.toggle("show_menu");
    }
    setActivePath(""); // Close the menu and reset the active path
  };   

  return (
    <header className="header" ref={headerRef}>
      <Row>
        <div className="nav_wrapper d-flex align-items-center justify-content-between">
          {/* Logo Start*/}
          <div className="Logo">
            <img src={logo} alt="" />
          </div>
          {/* Logo End */}

          {/* Menu Start*/}
          <div className="Navigation" ref={menuRef} onClick={toggleMenu}>
            <ul className="menu d-flex align-items-center gap-5">
              {nav_links.map((item, index) => (
                <li className={`nav_item${activePath === item.path ? " active_link" : ""}`}>
                <NavLink
                  to={item.path}
                  className={`nav_link${activePath === item.path ? " active_link" : ""}`}
                  onClick={() => setActivePath(item.path)}
                >
                  {item.display}
                </NavLink>
                </li>
              ))}
            </ul>
          </div>
          {/* Menu End */}

          <div className="nav_right d-flex align-items-center gap-4">
            {user !== null ? (
              <div className="nav_btn d-flex align-items-center gap-4">
                <div className="user_show">
                  <i className="ri-user-smile-fill"></i>
                </div>
                <Button className="btn btn-dark" onClick={handleLogout}>
                  Logout
                </Button>
              </div>
            ) : (
              <div className="nav_btn d-flex align-items-center gap-4">
                <Button className="btn secondary_btn">
                  <Link to="/login">Login</Link>
                </Button>
                <Button className="btn primary_btn">
                  <Link to="/register">Register</Link>
                </Button>
              </div>
            )}

            <span className="menu" onClick={toggleMenu}>
              <i className="ri-menu-line"></i>
            </span>
          </div>
        </div>
      </Row>
    </header>
  );
};

export default Header;
