import axios from 'axios';
import { BASE_URL } from '../utils/config';
import { useDispatch } from 'react-redux';

// Action types
export const LOGIN_START = 'LOGIN_START';
export const LOGIN_SUCCESS = 'LOGIN_SUCCESS';
export const LOGIN_FAILURE = 'LOGIN_FAILURE';
export const REGISTER_SUCCESS = 'REGISTER_SUCCESS';
export const LOGOUT = 'LOGOUT';

// Login action
export const login = (loginData) => {
  return async (dispatch) => {
    dispatch({ type: LOGIN_START });

    try {
      // Perform login request
      const response = await axios.post(`${BASE_URL}/auth/login`, loginData);

      // Handle successful login
      dispatch({ type: LOGIN_SUCCESS, payload: response.data.user });
      const user = response.data.user;
    setUser(user);
      // You can also store the authentication token or perform other necessary operations here
    } catch (error) {
      // Handle login failure
      dispatch({ type: LOGIN_FAILURE, payload: error.message });
    }
  };
};

// Register action
export const register = (registerData) => {
  return async (dispatch) => {
    dispatch({ type: LOGIN_START });

    try {
      // Perform registration request
      const response = await axios.post(`${BASE_URL}/auth/register`, registerData);

      // Handle successful registration
      dispatch({ type: REGISTER_SUCCESS });
      // You can perform any additional actions after successful registration
    } catch (error) {
      // Handle registration failure
      dispatch({ type: LOGIN_FAILURE, payload: error.message });
    }
  };
};

// Logout action
export const logout = () => {
  return (dispatch) => {
    // Handle logout
    dispatch({ type: LOGOUT });
    // You can also clear the authentication token or perform other necessary operations here
  };
};
