import React from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";

import "./tour-card.css";

const TourCard = ({ tour }) => {
  const { id, title, city, photo, price, featured, reviews } = tour;


  const calculateAvgRating = () => {
    if (reviews && reviews.length > 0) {
      const totalRating = reviews.reduce((acc, item) => acc + item.rating, 0);
      const avgRating = (totalRating / reviews.length).toFixed(1);
      return avgRating;
    }
    return 0;
  };

  const avgRating = calculateAvgRating();
  const ratingText = avgRating === 0 ? "Not rated" : avgRating;

  return (
    <div className="tour_card">
      <Card>
        <div className="tour_img">
          <img className="tour_img__image" src="https://picsum.photos/id/29/200/300" alt="tour-img" />
          {featured && <span>Featured</span>}
        </div>

        <CardBody>
          <div className="card_top d-flex align-items-center justify-content-between">
            <span className="tour_location d-flex align-items-center gap-1">
              <i className="ri-map-pin-line"></i> {city}
            </span>
            <span className="tour_rating d-flex align-items-center gap-1">
              <i className="ri-star-line"></i>{" "}
              {ratingText}
              {reviews && reviews.length === 0 ? null : <span>({reviews && reviews.length})</span>}
            </span>
          </div>

          <h5 className="tour_title">
            <Link to={`/tour/${id}`}>{title}</Link>
          </h5>

          <div className="card_bottom d-flex align-items-center justify-content-between mt-3">
            <h5>
              ${price} <span> /per person</span>
            </h5>

            <button className="btn booking_btn">
              <Link to={`/tour/${id}`}>Book Now</Link>
            </button>
          </div>
        </CardBody>
      </Card>
    </div>
  );
};

export default TourCard;
