import React from 'react';
import Slider from 'react-slick';
import ava1 from '/Users/tunjung/coding/MemoRide-SBD/frontend/src/assets/images/ava1.png';
import ava2 from '/Users/tunjung/coding/MemoRide-SBD/frontend/src/assets/images/ava2.png';

const Testimonial = () => {
  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    speed: 1000,
    swipeToSlide: true,
    autoplaySpeed: 2000,
    slidesToShow: 3,
    responsive: [
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <Slider {...settings}>
      <div className="testi py-4 px-3">
        <div className="tweet-container">
          <p className="tweet">
            Lorem ipsum dolor sit amet consectectur adipisicing elit. Minus sit,
            explicabo provident hic distinctio molestias voluptates nobis alias
            placeat suscipit earum debitis recusandae voluptate illum expendita
            corrupti aliquid doloribus delectus
          </p>

          <div className="tweet-author">
            <img src={ava1} className="avatar" alt="" />
            <div className="author-info">
              <h6 className="author-name">John Doe</h6>
              <p className="author-role">User</p>
            </div>
          </div>
        </div>
      </div>
      <div className="testi py-4 px-3">
        <div className="tweet-container">
          <p className="tweet">
            Lorem ipsum dolor sit amet consectectur adipisicing elit. Minus sit,
            explicabo provident hic distinctio molestias voluptates nobis alias
            placeat suscipit earum debitis recusandae voluptate illum expendita
            corrupti aliquid doloribus delectus
          </p>

          <div className="tweet-author">
            <img src={ava2} className="avatar" alt="" />
            <div className="author-info">
              <h6 className="author-name">Jane Doe</h6>
              <p className="author-role">User</p>
            </div>
          </div>
        </div>
      </div>
      {/* Add more testimonial slides as needed */}
    </Slider>
  );
};

export default Testimonial;
